mapper.update:
update event_info
set is_delete='1',
    update_time=#{updateTime},
    update_user_account=#{updateUserAccount}
where event_id = #{eventId};

mapper.select:
select *
from event_info e
where is_delete != '1';

select *
from event_info
where event_id = #{eventId}
  and is_delete != '1';

select * from event_info


        concat(IFNULL(event_name,''),IFNULL(occurred_place,'')) like concat('%',#{Map.nameOrPlace},'%')


        and DATE_FORMAT(occurred_time,'%Y-%m-%d') >= #{Map.beginTime}


        and DATE_FORMAT(occurred_time,'%Y-%m-%d') <= #{Map.endTime}


        and DATE_FORMAT(occurred_time,'%Y-%m-%d') >= #{Map.beginTime} and
        DATE_FORMAT(occurred_time,'%Y-%m-%d') <= #{Map.endTime}


        and is_handle=#{Map.isHandle}


        and is_settlement=#{Map.isSettlement}


        and DATE_FORMAT(settlement_time,'%Y-%m-%d') >= #{Map.beginSettlementTime}


        and DATE_FORMAT(settlement_time,'%Y-%m-%d') <= #{Map.endSettlementTime}


        and DATE_FORMAT(settlement_time,'%Y-%m-%d') >= #{Map.beginSettlementTime} and
        DATE_FORMAT(settlement_time,'%Y-%m-%d') <= #{Map.endSettlementTime}

    and is_delete!='1' order by occurred_time desc limit ${Page.page},${Page.pageSize} ;

select * from event_info e,
dm_tag_object_relation r,
dm_tag_object_info t


        concat(IFNULL(e.event_name,''),IFNULL(e.occurred_place,'')) like concat('%',#{Map.nameOrPlace},'%')


        and DATE_FORMAT(e.occurred_time,'%Y-%m-%d') >= #{Map.beginTime}


        and DATE_FORMAT(e.occurred_time,'%Y-%m-%d') <= #{Map.endTime}


        and DATE_FORMAT(e.occurred_time,'%Y-%m-%d') >= #{Map.beginTime} and
        DATE_FORMAT(occurred_time,'%Y-%m-%d') <= #{Map.endTime}


        and e.is_handle=#{Map.isHandle}


        and e.is_settlement=#{Map.isSettlement}


        and DATE_FORMAT(e.settlement_time,'%Y-%m-%d') >= #{Map.beginSettlementTime}


        and DATE_FORMAT(e.settlement_time,'%Y-%m-%d') <= #{Map.endSettlementTime}


        and DATE_FORMAT(e.settlement_time,'%Y-%m-%d') >= #{Map.beginSettlementTime} and
        DATE_FORMAT(settlement_time,'%Y-%m-%d') <= #{Map.endSettlementTime}

    and r.OBJECT_ID = e.event_id
    and r.TAG_ID = t.TAG_ID
    and r.TAG_ID in

        #{id}

    and e.is_delete!='1' and r.IS_DELETE!='1' and t.IS_DELETE!='1' limit
    ${Page.page},${Page.pageSize};;

select * from design_zcc.event_info


        concat(IFNULL('event_name',''),IFNULL('occurred_place','')) like concat('%',#{Map.nameOrPlace},'%')


        and DATE_FORMAT(occurred_time,'%Y-%m-%d') >= #{Map.beginTime}


        and DATE_FORMAT(occurred_time,'%Y-%m-%d') <= #{Map.endTime}


        and DATE_FORMAT(occurred_time,'%Y-%m-%d') >= #{Map.beginTime} and
        DATE_FORMAT(occurred_time,'%Y-%m-%d') <= #{Map.endTime}


        and is_handle=#{Map.isHandle}


        and is_settlement=#{Map.isSettlement}


        and DATE_FORMAT(settlement_time,'%Y-%m-%d') >= #{Map.beginSettlementTime}


        and DATE_FORMAT(settlement_time,'%Y-%m-%d') <= #{Map.endSettlementTime}


        and DATE_FORMAT(settlement_time,'%Y-%m-%d') >= #{Map.beginSettlementTime} and
        DATE_FORMAT(settlement_time,'%Y-%m-%d') <= #{Map.endSettlementTime}

    and is_delete!='1';

select *
from xhww_handle_log
where HANDLE_EVENT_ID = #{eventId}
  and IS_DELETE != '1'
limit ${page.page},${page.pageSize};

select *
from xhww_handle_log
where HANDLE_EVENT_ID = #{eventId}
  and IS_DELETE != '1';

select *
from xhww_handle_log
where HANDLE_LOG_ID = #{handleLogId}
limit 1;

mapper.update:
update xhww_handle_log
set IS_DELETE='1'
where HANDLE_LOG_ID = #{handleLogId};
