mapper.insert:

    select replace(uuid(),'-','') from dual;

insert into unit_info values
(#{unitId},#{unitName},#{unitDetail},#{unitLegalPerson},#{usccCode},#{status},#{industry},#{addr},#{registerTime},'0');

mapper.update:
update unit_info
set is_delete='1'
where unit_id = #{unitId};

update unit_info


        unit_name=#{unitName}


        unit_detail=#{unitDetail}


        unit_legal_person=#{unitLegalPerson}


        uscc_code=#{usccCode}


        status=#{status}


        industry=#{industry}


        addr=#{addr}


        register_time=#{registerTime}


where unit_id=#{unitId};

mapper.select:
select *
from unit_info
where unit_id = #{unitId}
  and is_delete != '1';

select *
from unit_info
where status = #{status}
  and is_delete != '1';

select * from unit_info
concat(ifnull(unit_name,''),ifnull(unit_legal_person,''),ifnull(industry,''),ifnull(addr),'') like concat('%',#{param},'%')
and is_delete!='1';

select p.unit_id, p.unit_name, p.unit_detail, p.unit_legal_person, p.uscc_code, p.status, p.industry, p.addr, p.register_time, p.is_delete from unit_info p, dm_tag_object_relation r, dm_tag_object_info t
concat(ifnull(p.unit_name,''),ifnull(p.unit_legal_person,''),ifnull(p.industry,''),ifnull(p.addr),'') like concat('%',#{param},'%')
and r.TAG_ID in
#{tagId}
and p.unit_id = r.OBJECT_ID and r.TAG_ID=t.TAG_ID and r.OBJECT_TYPE = 'unit' and p.is_delete != '1' and r.IS_DELETE!='1' and t.IS_DELETE!='1';

select * from unit_info
concat(ifnull(unit_name,''),ifnull(unit_legal_person,''),ifnull(industry,''),ifnull(addr),'') like concat('%',#{param},'%')
and is_delete!='1' limit ${page.page},${page.pageSize};

select p.unit_id, p.unit_name, p.unit_detail, p.unit_legal_person, p.uscc_code, p.status, p.industry, p.addr, p.register_time, p.is_delete from unit_info p, dm_tag_object_relation r, dm_tag_object_info t
concat(ifnull(p.unit_name,''),ifnull(p.unit_legal_person,''),ifnull(p.industry,''),ifnull(p.addr),'') like concat('%',#{param},'%')
and r.TAG_ID in
#{tagId}
and p.unit_id = r.OBJECT_ID and r.TAG_ID=t.TAG_ID and r.OBJECT_TYPE = 'unit' and p.is_delete != '1' and r.IS_DELETE!='1' and t.IS_DELETE!='1' limit ${page.page},${page.pageSize};
